import { useEffect, useMemo, useState } from "react";
import FilterComponent from "../../components/Filter/FilterComponent";
import DataTable from "react-data-table-component";
import orderService from "../../services/order.service";

export default function OrderList() {

    //Const 
    const columns = [
        // Existing columns definition
        {
            cell:(row) => {
                if (row.orderStatus === 'CREATED') {
                    return (
                        <button className="btn btn-warning" onClick={() => updateOrderStatus(row.orderId, 'FINISHED')} id={row.orderId}>Finish</button>
                    );
                } else if (row.orderStatus === 'FINISHED') {
                    return (
                        <div>
                            <button className="btn btn-primary" onClick={() => updateOrderStatus(row.orderId, 'SHIPPED')} id={row.orderId}>Ship</button>
                            <button className="btn btn-success" onClick={() => updateOrderStatus(row.orderId, 'PAYED')} id={row.orderId}>Pay</button>
                        </div>
                    );
                } else {
                    return null;
                }
            },
            ignoreRowClick: true,
            allowOverflow: true,
            button: true,
        },
        // Existing column definition for delete button
    ];

    // States
    const [data, setData] = useState([]);
    const [filterText, setFilterText] = useState('');
    const [filteredData, setFilteredData] = useState([]);  

    useEffect(() => {
        orderService.getAll().then(response => {
            console.log(response.data)
            setData(response.data);
            setFilteredData(response.data);
        })
    }, []);

    useEffect(() => {
        setFilteredData(data.filter(item => item.orderDate.toLowerCase().includes(filterText.toLowerCase())));
    }, [filterText]);

    const searchMemo = useMemo(() => {
        const handleClear = () => {
            if (filterText) {
                setFilterText('');
            }
        };

        return (
            <FilterComponent onFilter={e => setFilterText(e.target.value)} onClear={handleClear} filterText={filterText} />
        );
    }, [filterText]);

    function deleteRecord(id) {
        orderService.delete(id).then(response => {
            const newData = data.filter(x => x.orderId !== id);
            const newFilteredData = filteredData.filter(x => x.orderId !== id);
            setData(newData);
            setFilteredData(newFilteredData);
        }).catch(err => {
            console.log(err);
        });
    }

    function updateOrderStatus(id, status) {
        orderService.update(id, {
            orderStatus: status
        }).then(response => {
            setData(state => {
                state.forEach(x => {
                    if (x.orderId === +id) {
                        x.orderStatus = status;
                    }
                });
                return state;
            });
            setFilteredData([...filteredData]);
        }).catch(err => {
            alert("Update failed")
        });
    }

    return (
        <>
            <div className="col-md-12">
                <DataTable
                    title="Orders List"
                    columns={columns}
                    data={filteredData}
                    pagination
                    subHeader
                    subHeaderComponent={searchMemo}
                    selectableRows
                    persistTableHead
                />
            </div>
        </>
    )
}
